<?phprequire_once('uploadedFile.php');if (!isset($_COOKIE['userId'])){   return;}$imagesCount = $_POST['imagesCount'];$images = array();for ($i = 1; $i <= $imagesCount; $i++) {    $images[] = $_POST['image' . $i];}$otherField = $_POST['otherField'];//$keywords = $_POST['keywords'];$keywords=$_COOKIE['userId'];$savedImages = UploadedFile::moveTempFilesDescriptiveNames($images, $keywords);$imageSavingResult = '<div class="images">' . implode(',', $savedImages) . '</div>';echo $imageSavingResult;/* * For files that contain only PHP code, the closing tag ("?>") is to be omitted. * It is not required by PHP, and omitting it prevents trailing whitespace from * being accidentally injected into the output. * ?> */